import React from "react";
import logo from "logo4.svg";
import {
    Button,
    Card,
    CardHeader,
    CardBody,
    FormGroup,
    Form,
    Input,
    Row,
    Col,
    CustomInput,
    Label
} from "reactstrap";

class Badge extends React.Component {
//                            {/* <img alt="..." src={require("assets/img/bg5.jpg")} /> */}

    state={
        selectedFile:null
    }

fileSelectedHandler=event=>{
   this.setState({
       selectedFile:event.target.files[0]
   })
   console.log()
}

fileUploadHandler=event=>{
    debugger
        console.log(event.target.files[0]);
    }

    render() {
        var props = this.props.datos;
        return (
            <>
                <Col md="4">
                    <Card className="card-user">
                        <div className="image">
                        </div>
                        <CardBody>
                            <div className="author">
                                <a href="#pablo" onClick={e => e.preventDefault()}>
                                    <img
                                        alt="..."
                                        className="avatar border-gray"
                                        src={logo}
                                    />
                                    <h5 className="title">{props.nombre}</h5>
                                </a>
                                <p className="description">{props.usuario}</p>
                            </div>
                            <p className="description text-center">
                                {props.correo}
                            </p>
                        </CardBody>
                        <hr />
                        <div className="button-container">
                            <input onChange={this.fileSelectedHandler} style={{display:'none'}} type="file" ref={fileinput=>this.fileinput=fileinput}/>
                            <Button
                                className="btn-neutral btn-icon btn-round"
                                color="default"
                                href="#pablo"
                                onClick={()=>{this.fileinput.click()}}
                                size="lg"
                            >
                                <i className="fas fa-camera-retro" />
                            </Button>
                        </div>
                    </Card>
                </Col>
            </>
        );
    }
}

// export default graphql(getEmpresas)(TablesEmpresas);
export default Badge;
