import React from "react";
import logo from "logo4.svg";
import {
    Button,
    Card,
    CardHeader,
    CardBody,
    FormGroup,
    Form,
    Input,
    Row,
    Col,
    CustomInput,
    Label
} from "reactstrap";

class Badge extends React.Component {
    //                            {/* <img alt="..." src={require("assets/img/bg5.jpg")} /> */}

    state = {
        selectedFile: null
    }

    textInput = React.createRef();


    fileSelectedHandler = event => {
        debugger
        this.setState({
            selectedFile: event.target.files[0]
        })
        console.log(event)
    }

    fileUploadHandler = event => {
        debugger
        console.log(event.target.files[0]);
    }

    focusTextInput() {
        // Explicitly focus the text input using the raw DOM API
        // Note: we're accessing "current" to get the DOM node
        if (this.textInput) this.textInput.click();
    };

    render() {
        var props = this.props.datos;
        return (
            <>
                <Col md="4">
                    <Card className="card-user">
                        <div className="image">
                        </div>
                        <CardBody>
                            <div className="author">
                                <a href="#pablo" onClick={e => e.preventDefault()}>
                                    <img
                                        alt="..."
                                        className="avatar border-gray"
                                        src={this.state.selectedFile}
                                    />
                                    <h5 className="title">{props.nombre}</h5>
                                </a>
                                <p className="description">{props.usuario}</p>
                            </div>
                            <p className="description text-center">
                                {props.correo}
                            </p>
                        </CardBody>
                        <hr />
                        <div className="button-container">
                            <input onChange={(event) => this.fileSelectedHandler(event)} style={{ display: '' }} type="file"
                                innerRef={input => (this.nameRef = input)} />
                            <Button
                                className="btn-neutral btn-icon btn-round"
                                color="default"
                                href="#pablo"
                                onClick={this.focusTextInput}
                                size="lg"
                            >
                                <i className="fas fa-camera-retro" />
                            </Button>
                        </div>
                    </Card>
                </Col>
            </>
        );
    }
}

// export default graphql(getEmpresas)(TablesEmpresas);
export default Badge;
