import 'react-notifications/lib/notifications.css';
import Badgeuser from "components/badges/badgeUser";
import Formuser from "components/forms/userform"


class Userdetails extends React.Component {

    state = {}

    componentDidMount() {
        this.setState({
            Id:this.props.datos._id,
            nombre: this.props.datos.nombre,
            correo: this.props.datos.correo,
            provincia: this.props.datos.provincia,
            canton: this.props.datos.canton,
            distrito: this.props.datos.distrito,
        })

    }
    handleChange = e => {
        this.setState({
            ... this.state,
            [e.target.name]: e.target.value,
        });
    };

