import React from "react";
import logo from "logo4.svg";
import {
    Button,
    Card,
    CardHeader,
    CardBody,
    FormGroup,
    Form,
    Input,
    Row,
    Col,
    CustomInput,
    Label
} from "reactstrap";

var textInput = React.createRef();

class Badge extends React.Component {
    //                            {/* <img alt="..." src={require("assets/img/bg5.jpg")} /> */}

    state = {
        selectedFile: null
    }



    fileSelectedHandler = event => {
        debugger
        this.setState({
            selectedFile: event.target.files[0]
        })
    }

    fileUploadHandler = event => {
        debugger
        console.log(event.target.files[0]);
    }

    handleClick = (e) => {
        this.inputElement.click();
      }

    render() {
        var props = this.props.datos;
        return (
            <>
                <Col md="4">
                    <Card className="card-user">
                        <div className="image">
                        </div>
                        <CardBody>
                            <div className="author">
                                <a href="#pablo" onClick={e => e.preventDefault()}>
                                    <img
                                        alt="..."
                                        className="avatar border-gray"
                                        src={this.state.selectedFile}
                                    />
                                    <h5 className="title">{props.nombre}</h5>
                                </a>
                                <p className="description">{props.usuario}</p>
                            </div>
                            <p className="description text-center">
                                {props.correo}
                            </p>
                        </CardBody>
                        <hr />
                        <div className="button-container">
                            <input onChange={(event) => this.fileSelectedHandler(event)} style={{ display: 'none' }} type="file"
                                ref={input => this.inputElement = input} />
                            <button
                                className="btn btn-dark btn-lg btn-icon btn-round"                                href="#pablo"
                                onClick={this.handleClick}
                            >
                                <i className="fas fa-camera-retro" />
                            </button>
                        </div>
                    </Card>
                </Col>
            </>
        );
    }
}

// export default graphql(getEmpresas)(TablesEmpresas);
export default Badge;
