/*!

=========================================================
* Now UI Dashboard React - v1.2.0
=========================================================

* Product Page: https://www.creative-tim.com/product/now-ui-dashboard-react
* Copyright 2019 Creative Tim (https://www.creative-tim.com)
* Licensed under MIT (https://github.com/creativetimofficial/now-ui-dashboard-react/blob/master/LICENSE.md)

* Coded by Creative Tim

=========================================================

* The above copyright notice and this permission notice shall be included in all copies or substantial portions of the Software.

*/
import React, { useState, useEffect, Fragment } from 'react';
// reactstrap components
import {Row} from "reactstrap";
import { gql } from 'apollo-boost'
// core components
import PanelHeader from "components/PanelHeader/PanelHeader.jsx";
import EmpresaDetalles from "components/Empresa/EmpresaDetalles.jsx";
import queries from 'queries/Empresas.jsx';
import { useQuery, useMutation } from '@apollo/react-hooks';
import Badge from "components/Badges/Badge.jsx";
import EmpresaForm from "components/Forms/EmpresaForm.jsx"



const UserEdit=(props)=> {

    const Id = props.match.params.empresaid;
    const getEmpresa = gql`${queries.getEmpresa}`;
    const updateEmpresa = gql`${queries.updateEmpresa}`;
    const Initialstate = {
        nombre: '',
        cedulajuridica: '',
        correo: '',
        provincia: '',
        canton: '',
        distrito: '',
        telefono:'',
        logo:''
    }

    const [empresa,grdEmpresa]=useState(Initialstate);

    const handleChange = e => {
        grdEmpresa({
            ... empresa,
            [e.target.name]: e.target.value,
        });
    };

   const fileSelectedHandler = event => {
        this.setState({
            logo:URL.createObjectURL(event.target.files[0])
        })
      }

   const GetEmpresa = ({Id}) => {

    const {loading, error, data  }  = useQuery(getEmpresa,{variables:{Id}});
    const empresa = data === undefined ? [] : data.empresa;

    empresa.logo

    if (loading) return <p>loading...</p>;
    if (error) return <p>{error.message}</p>;

        // return (
        //     <React.Fragment>
        //         <EmpresaDetalles onChange={handleChange} query={updateEmpresa} title={'Editar Empresa'} ident={Id} datos={empresa} />
        //     </React.Fragment>
        // )

        return (
            <>

                <React.Fragment>
                    <EmpresaForm onChange={handleChange} title={'Editar Empresa'} datos={empresa} query={updateEmpresa} />
                    <Badge selectedFile={fileSelectedHandler} datos={empresa} />
                </React.Fragment>

            </>
        );
    }

        return (
            <>
                <PanelHeader size="sm" />
                <div className="content">
                    <Row>
                        <GetEmpresa Id={Id} />
                    </Row>
                </div>
            </>
        );
}

export default UserEdit;
