import React from 'react';
import ReactDOM from 'react-dom';
import 'assets/css/modal.css';
import { Button, Card, ModalHeader,ModalBody,ModalFooter,CardHeader, CardBody, FormGroup, Form, Input, Modal, Row, Col } from "reactstrap";
import queries from 'queries/Empresas.jsx';
import { gql } from 'apollo-boost'
import { Mutation } from "react-apollo";
import { createNotification } from "components/notifications/notifications.jsx"



//Cambiar el portal
const deleteEmpresas = gql`${queries.deleteEmpresa}`;

const Modalsimple = (props) => {

    if (!props.isOpen) {
        return null;
    }
    const deleteEmpresas = gql`${queries.deleteEmpresa}`;
    const _id=this.props.ID;
    console.log("SI llego a modalsimple");
    //Permite que en un futuro especifiquemos de q trata el modal  props.children
    return (

        <Modal isOpen={props.isOpen}  className="Modal">
            <ModalHeader >Est√° seguro?</ModalHeader>
            <ModalBody>
            Usted esta por borrar una empresa de la lista
        </ModalBody>
            <ModalFooter>
                <Button color="primary" onClick={props.onClose} >DCancelar</Button>{' '}
                <Mutation mutation={deleteEmpresas} variables={{ _id }} onCompleted={this.props.onClose} onError={createNotification("error")}>
                        {postEmpresa =>
                            <Button
                                className="text-reset text-decoration-none ml-1 btn btn-outline-danger"
                                onClick={postEmpresa}
                            >Borrar
                            </Button>
                        }   
                    </Mutation>
            </ModalFooter>
        </Modal>

        //     <Modal>

        //         <button onClick={props.onClose} className="Modal__close-button">
        //             X
        // </button>

        //         {props.children}
        //     </Modal>
        // document.getElementById('modal')
    );
}

export default Modalsimple;
