import React, { Component, Fragment, useState } from "react";

import {
  Button,
  Card,
  CardHeader,
  CardBody,
  FormGroup,
  Form,
  Input,
  Row,
  Col
} from "reactstrap";
import {graphql} from 'react-apollo';


// import gql from "graphql-tag";
import FormToast from "components/forms/userform.jsx";
import queries from 'queries/Empresas.jsx';
import { ToastProvider, useToasts } from 'react-toast-notifications'




class Formuser extends React.Component {

  state = {
    nombre: '',
    usuario: '',
    correo: '',
    provincia: '',
    canton: '',
    distrito: '',
  } 

  handleChange=e=>{

    this.setState({
             ... this.state.form,
            [e.target.name]:e.target.value,
    });
};


  render() {
    const {nombre,usuario,correo, provincia, canton, distrito} = this.state

    return (
      <>
        <ToastProvider>
        <Col md="8">
          <Card>
            <CardHeader>
              <h5 className="title">Registro de empresas</h5>
            </CardHeader>
            <CardBody>
            <FormToast/>
            </CardBody>
          </Card>
        </Col>
        </ToastProvider>
      </>
    );
  }
}

export default graphql(queries.addEmpresas)(Formuser);
