import React, { Component, Fragment, useState } from "react";
import 'react-notifications/lib/notifications.css';
import {NotificationContainer, NotificationManager} from 'react-notifications';
import {Button,Card,CardHeader,CardBody,FormGroup,Form,Input,Row,Col} from "reactstrap";
import {graphql} from 'react-apollo';
import { Mutation } from "react-apollo";
import queries from 'queries/Empresas.jsx';
import { AvForm, AvField } from 'availity-reactstrap-validation';

class Formuser extends React.Component {

createNotification = (type) => {
  return () => {
    switch (type) {
      case 'info':
        NotificationManager.info('Info message');
        break;
      case 'success':
        NotificationManager.success('Success message', 'Registro Exitoso');
        break;
      case 'warning':
        NotificationManager.warning('Warning message', 'No se complet贸 el registro por falta de datos', 3000);
        break;
      case 'error':
        NotificationManager.error('Error message', 'Datos incompletos!', 5000, () => {
          alert('callback');
        });
        break;
    }
  };
};


  // handleSubmit =e=>{


  //   const props=this.props.datos;
  //   const {nombre,usuario,correo, provincia, canton, distrito} = props

  //   if(nombre,usuario,correo, provincia, canton, distrito===''){
  //     this.createNotification('error');
  //     return;         
  // }}

  render() {

    const props=this.props.datos;
    const onChange=this.props.onChange;
    const {nombre,usuario,correo, provincia, canton, distrito} = props


    return (
      <>
        <Col md="8">
          <Card>
            <CardHeader>
              <h5 className="title">{this.props.title}</h5>
            </CardHeader>
            <CardBody>
              <AvForm onSubmit={this.handleSubmit} >
                <Row>
                  <Col className="px-1" md="3">
                    <FormGroup>
                      <label>Nombre</label>
                      <AvField
                        placeholder="nombre"
                        type="text"
                        name="nombre"
                        onChange={onChange}
                        required
                      />
                    </FormGroup>
                  </Col>
                  <Col className="pl-1" md="4">
                    <FormGroup>
                      <label htmlFor="exampleInputEmail1">
                        Correo
                          </label>
                      <AvField placeholder="Email" type="email" name="correo" onChange={onChange}
                      required
                       />
                    </FormGroup>
                  </Col>
                  <Col className="pr-1" md="4">
                    <FormGroup>
                      <label>Usuario</label>
                      <AvField
                                              placeholder="Company"
                        type="text"
                        name="usuario"
                        onChange={onChange}
                        required
                      />
                    </FormGroup>
                  </Col>
                </Row>
                <Row>
                  <Col className="pl-1" md="4">
                    <FormGroup>
                      <label>Provincia</label>
                      <AvField
                        defaultValue="Andrew"
                        placeholder="Last Name"
                        type="text"
                        name="provincia"
                        onChange={onChange}
                        required
                      />
                    </FormGroup>
                  </Col>
                  <Col className="px-1" md="4">
                    <FormGroup>
                      <label>Cant贸n</label>
                      <AvField
                        defaultValue="Andrew"
                        placeholder="Country"
                        type="text"
                        name="canton"
                        onChange={onChange}
                        required
                      />
                    </FormGroup>
                  </Col>
                  <Col className="px-1" md="4">
                    <FormGroup>
                      <label>Distrito</label>
                      <AvField
                        placeholder="Country"
                        type="text"
                        name="distrito"
                        onChange={onChange}
                        required
                      />
                    </FormGroup>
                  </Col>
                </Row>
                <Row>
                  <Col md="12">
                    <FormGroup>
                      <label>Direcci贸n</label>
                      <AvField
                        placeholder="Home Address"
                        type="text"
                        name="direccion"
                        required
                      />
                    </FormGroup>
                  </Col>
                </Row>
                <Row>
                  <Col className="pr-1" md="4">
                    <FormGroup>
                      <label>Distrito</label>
                      <AvField
                        placeholder="City"
                        type="text"
                        name="no"
                        required
                      />
                    </FormGroup>
                  </Col>

                  <Col className="pl-1" md="4">
                    <FormGroup>
                      <label>N.Cuenta</label>
                      <AvField placeholder="x-xxxxxxx-xxx-x" type="text" name="cuenta" required/>
                    </FormGroup>
                  </Col>
                </Row>
                <Mutation mutation={queries.addEmpresas}  variables={{nombre, correo, provincia, canton, distrito}} onCompleted={this.createNotification('success')} onError={this.createNotification('error')}>
                  {postEmpresa=>
                  <Row>
                    <Col md="12">
                      <FormGroup>
                        <Button color="primary" onClick={postEmpresa}>Guardar</Button>
                      </FormGroup>
                    </Col>
                  </Row>
                  }
                </Mutation>
                <NotificationContainer/>
              </AvForm>
            </CardBody>
          </Card>
        </Col>
      </>
    );
  }
}

export default graphql(queries.addEmpresas)(Formuser);
