import React from "react";
// import { Query } from "react-apollo";



// reactstrap components
import {
  Card,
  CardBody,
  CardHeader,
  CardTitle,
  // Table,
  Row,
  Col
} from "reactstrap";

// core components
import PanelHeader from "components/PanelHeader/PanelHeader.jsx";
import Table from 'components/tables/Tablelist.jsx';
import { thead, tbody } from "variables/general";
import queries from 'queries/Empresas.jsx';
import { graphql } from 'react-apollo';

//Se importa el query
const getEmpresas = queries.getEmpresas;

class RegularTables extends React.Component {

  state = {
    loading: true,
    error: null,
    data: [],
  };

  componentDidMount() {
    console.log(this.props)
    this.fetchData();
  }

  // getData = async () => {
  //   return (
  //     <Query query={queries.getEmpresas}>
  //     {({ loading, error, data }) => {
  //       if (loading) return <div>Fetching</div>
  //       if (error) return <div>Error</div>

  //       const empresas = data.data.empresas
  //       this.setState({ loading: false, data: empresas });
  //     }}
  //   </Query>

  //   )
  // }

  fetchData = async () => {
    this.setState({ loading: true, error: null });
    try {
      this.setState({ loading: false, data: this.props.data.empresas });
    } catch (error) {
      this.setState({ loading: false, error: error });
    }
  };


  render() {
    return (
      <>
        <PanelHeader size="sm" />
        <div className="content">
          <Row>
            <Col xs={12}>
              <Card>
                <CardHeader>
                  <CardTitle tag="h4">Empresas registradas</CardTitle>
                </CardHeader>
                <CardBody>
                  <Table empresadata={this.state} />
                </CardBody>
              </Card>
            </Col>
            <Col xs={12}>
              <Card className="card-plain">
                <CardHeader>
                  <CardTitle tag="h4">Table on Plain Background</CardTitle>
                  <p className="category"> Here is a subtitle for this table</p>
                </CardHeader>
                <CardBody>



                  <Table empresadata={this.state} />
                </CardBody>
              </Card>
            </Col>
          </Row>
        </div>
      </>
    );
  }
}

// export default RegularTables;
export default graphql(getEmpresas)(RegularTables);
