    Button,
    Card,
    CardHeader,
    CardBody,
    FormGroup,
    Form,
    Input,
    Row,
    Col
} from "reactstrap";
import { gql } from 'apollo-boost'
// core components
import PanelHeader from "components/PanelHeader/PanelHeader.jsx";
import EmpresaDetalles from "components/Empresa/EmpresaDetalles.jsx";
import queries from 'queries/Empresas.jsx';
import { Query } from "react-apollo"
import uuid from 'uuid';
import { useQuery, useMutation } from '@apollo/react-hooks';

const getEmpresa = gql`${queries.getEmpresa}`;
const updateEmpresa = gql`${queries.updateEmpresa}`;



class UserEdit extends React.Component {

    state = {
        nombre: '',
        usuario: '',
        correo: '',
        provincia: '',
        canton: '',
        distrito: '',
    }

    handleChange = e => {
        this.setState({
            ... this.state,
            [e.target.name]: e.target.value,
        });
    };

    GetEmpresa = ({id}) => {

        const { data, error, loading }  = useQuery(getEmpresa,{variables:{id}});

        return (
            <React.Fragment>
                <EmpresaDetalles onChange={this.handleChange} query={updateEmpresa} title={'Editar Empresa'} ident={id} datos={data} />
            </React.Fragment>
        )
    }

    render() {

