                                         <EmpresaDetalles onChange={this.handleChange} query={updateEmpresa} title={'Editar Empresa'} ident={Id} datos={datos} />
                                     </React.Fragment>
                                    <this.GetEmpresa id={Id} />
                                )
                            {/* }}
                        </Query> */}
                    </Row>
                </div>
            </>
        );
    }
}

export default UserEdit;
