import { Table } from "reactstrap";
import { thead, tbody } from "variables/general";
import React from "react";
import { Query } from "react-apollo"
import { graphql } from 'react-apollo';
import queries from 'queries/Empresas.jsx';
import { gql } from 'apollo-boost'

const getEmpresas = gql`${queries.getEmpresas}`;

class TablesEmpresas extends React.Component {

    state = {
        loading: true,
        error: null,
        empresas: [],
    };


    componentDidMount() {
        this.fetchData();
        this.intervalId = setInterval(this.fetchData, 5000);
    }

    componentWillUnmount() {
        clearInterval(this.intervalId);
    }

    fetchData() {
        <Query key={4} query={getEmpresas}>
            {({ loading, error, data }) => {

            this.setState({ loading: loading, error: error, empresas: data.empresas});
            }}
        </Query>
    }

    showEmpresas() {

        // return (
        //     <Query key={3} query={getEmpresas}>
        //         {({ loading, error, data }) => {

        //             if (loading) return <p>loading...</p>;
        //             if (error) return <p>{error.message}</p>;
        //             return (
        //                 <tbody key={1}>
        //                     {data.empresas.map((element) => {
        //                         return (

        //                             <tr key={element._id}>
        //                                 <th key={element._id} className="text-right">
        //                                     {element.nombre}
        //                                 </th>

        //                                 <th key={element._id} className="text-right">
        //                                     {element.correo}
        //                                 </th>

        //                                 <th key={element._id} className="text-right">
        //                                     {element.canton}
        //                                 </th>

        //                                 <th key={element._id} className="text-right">
        //                                     {element.provincia}
        //                                 </th>
        //                             </tr>
        //                         )
        //                     })}
        //                 </tbody>
        //             )
        //         }}
        //     </Query>
        // )

        {({ loading, error, data }) => {

                        if (loading) return <p>loading...</p>;
                        if (error) return <p>{error.message}</p>;
                        return (
                            <tbody key={1}>
                                {data.empresas.map((element) => {
                                    return (

                                        <tr key={element._id}>
                                            <th key={element._id} className="text-right">
                                                {element.nombre}
                                            </th>

                                            <th key={element._id} className="text-right">
                                                {element.correo}
                                            </th>

                                            <th key={element._id} className="text-right">
                                                {element.canton}
                                            </th>

                                            <th key={element._id} className="text-right">
                                                {element.provincia}
                                            </th>
                                        </tr>
                                    )
                                })}
                            </tbody>
                        )
                    }}

    }

    render() {
        return (
            <>
                <Table key={2} responsive>
                    <thead className="text-primary">
                        <tr>
                            {thead.map((prop, key) => {
                                if (key === thead.length - 1)
                                    return (
                                        <th key={key} className="text-right">
                                            {prop}
                                        </th>
                                    );
                                return <th key={key}>{prop}</th>;
                            })}
                        </tr>
                    </thead>
                    {this.showEmpresas()}
                </Table>
            </>
        );
    }
}

export default graphql(getEmpresas)(TablesEmpresas);
