import React, { Component, Fragment, useState } from "react";
import 'react-notifications/lib/notifications.css';
import { NotificationContainer, NotificationManager } from 'react-notifications';
import { Button, Card, CardHeader, CardBody, FormGroup, Form, Input, Row, Col } from "reactstrap";
import { graphql } from 'react-apollo';
import { Mutation } from "react-apollo";
import queries from 'queries/Empresas.jsx';
import { AvForm, AvField } from 'availity-reactstrap-validation';
import Badgeuser from "components/badges/badgeUser";



class Formuser extends React.Component {

  state = {}

    createNotification = (type) => {
        return () => {
        switch (type) {
            case 'info':
            NotificationManager.info('Info message');
            break;
            case 'success':
            NotificationManager.success('Success message', 'Registro Exitoso');
            break;
            case 'warning':
            NotificationManager.warning('Warning message', 'No se completÃ³ el registro por falta de datos', 3000);
            break;
            case 'error':
            NotificationManager.error('Error message', 'Datos incompletos!', 5000, () => {
                alert('callback');
            });
            break;
        }
        };
    };

  componentDidMount() {
    this.setState({
      nombre: this.props.datos.nombre,
      correo: this.props.datos.correo,
      provincia: this.props.datos.provincia,
      canton: this.props.datos.canton,
      distrito: this.props.datos.distrito,
    })

  }
  handleChange = e => {
    this.setState({
        ... this.state,
        [e.target.name]: e.target.value,
    });
};

  render() {

    const { nombre, usuario, correo, provincia, canton, distrito }= this.state;
    const onChange = this.handleChange;
    const datosestado = this.state;


    return (
        <>
        <Col md="8">
          <Card>
            <CardHeader>
              <h5 className="title">{this.props.title}</h5>
            </CardHeader>
            <CardBody>
              <AvForm >
                <Row>
                  <Col className="px-1" md="3">
                    <FormGroup>
                      <label>Nombre</label>
                      <AvField 
                        placeholder="nombre"
                        type="text"
                        name="nombre"
                        onChange={onChange}
                        value={datosestado.nombre}
                        required
                      />
                    </FormGroup>
                  </Col>
                  <Col className="pl-1" md="4">
                    <FormGroup>
                      <label htmlFor="exampleInputEmail1">
                        Correo
                          </label>
                      <AvField  
                       placeholder="Email" 
                       type="email" 
                       name="correo" 
                       onChange={onChange}
                       value={datosestado.correo}
                      required
                      />
                    </FormGroup>
                  </Col>
                  <Col className="pr-1" md="4">
                    <FormGroup>
                      <label>Usuario</label>
                      <AvField 
                        placeholder="Company"
                        type="text"
                        name="usuario"
                        onChange={onChange}
                        value={datosestado.usuario}
                        required
                      />
                    </FormGroup>
                  </Col>
                </Row>
                <Row>
                  <Col className="pl-1" md="4">
                    <FormGroup>
                      <label>Provincia</label>
                      <AvField 
                        placeholder="Last Name"
                        type="text"
                        name="provincia"
                        value={datosestado.provincia}
                        onChange={onChange}
                        required
                      />
                    </FormGroup>
                  </Col>
                  <Col className="px-1" md="4">
                    <FormGroup>
                      <label>CantÃ³n</label>
                      <AvField 
                        placeholder="Country"
                        type="text"
                        name="canton"
                        onChange={onChange}
                        value={datosestado.canton}
                        required
                      />
                    </FormGroup>
                  </Col>
                  <Col className="px-1" md="4">
                    <FormGroup>
                      <label>Distrito</label>
                      <AvField 
                        placeholder="Country"
                        type="text"
                        name="distrito"
                        onChange={onChange}
                        value={datosestado.distrito}
                        required
                      />
                    </FormGroup>
                  </Col>
                </Row>
                <Row>
                  <Col md="12">
                    <FormGroup>
                      <label>DirecciÃ³n</label>
                      <AvField 
                        placeholder="Home Address"
                        type="text"
                        name="direccion"
                        required
                      />
                    </FormGroup>
                  </Col>
                </Row>
                <Row>
                  <Col className="pr-1" md="4">
                    <FormGroup>
                      <label>Distrito</label>
                      <AvField 
                        placeholder="City"
                        type="text"
                        name="no"
                        required
                      />
                    </FormGroup>
                  </Col>

                  <Col className="pl-1" md="4">
                    <FormGroup>
                      <label>N.Cuenta</label>
                      <AvField  placeholder="x-xxxxxxx-xxx-x" type="text" name="cuenta" required />
                    </FormGroup>
                  </Col>
                </Row>
                <Mutation mutation={queries.addEmpresas} variables={{ nombre, correo, provincia, canton, distrito }} onCompleted={this.createNotification('success')} onError={this.createNotification('error')}>
                  {postEmpresa =>
                    <Row>
                      <Col md="12">
                        <FormGroup>
                          <Button color="primary" onClick={postEmpresa}>Guardar</Button>
                        </FormGroup>
                      </Col>
                    </Row>
                  }
                </Mutation>
                <NotificationContainer />
              </AvForm>
            </CardBody>
          </Card>
        </Col>
        <Badgeuser datos={this.state} />
      </>
    );
  }
}

export default graphql(queries.addEmpresas)(Formuser);
