import React from "react";

// reactstrap components
import {
    Button,
    Card,
    CardHeader,
    CardBody,
    FormGroup,
    Form,
    Input,
    Row,
    Col
} from "reactstrap";
import { gql } from 'apollo-boost'


// core components
import PanelHeader from "components/PanelHeader/PanelHeader.jsx";
import Formuser from "components/forms/userform.jsx";
import Badgeuser from "components/badges/badgeUser";
import queries from 'queries/Empresas.jsx';
import { Query } from "react-apollo"
import uuid from 'uuid';


const getEmpresa = gql`${queries.getEmpresa}`;



class UserEdit extends React.Component {

    state = {
        nombre: '',
        usuario: '',
        correo: '',
        provincia: '',
        canton: '',
        distrito: '',
    }

    componentDidMount() {


    }

    fetchdata() {

    }

    handleChange = e => {
        this.setState({
            ... this.state.form,
            [e.target.name]: e.target.value,
        });

    };

    render() {

        const Id = this.props.match.params.empresaid;

        return (
            <>
                <PanelHeader size="sm" />
                <div className="content">
                    <Row>
                        <Query key={uuid()} query={getEmpresa} variables={{ Id }} >
                            {({ loading, error, data }) => {

                                const datos=data.empresa;


                                    // this.setState({
                                    //     nombre: data.empresa.nombre,
                                    //     usuario: data.empresa.usuario,
                                    //     correo: data.empresa.correo,
                                    //     provincia: data.empresa.provincia,
                                    //     canton: data.empresa.canton,
                                    //     distrito: data.empresa.distrito,
                                    // })
                                    // if (loading) return <p>loading...</p>;
                                    // if (error) return <p>{error.message}</p>;
                                    return (
                                        <React.Fragment>
                                            <Formuser onChange={this.handleChange} title={'Editar Empresa'} datos={datos} />
                                            <Badgeuser datos={datos} />
                                        </React.Fragment>
                                    )
                                }

                            }}
                        </Query>
                    </Row>
                </div>
            </>
        );
    }
}

export default UserEdit;
