import React from "react";

import {
  Button,
  Card,
  CardHeader,
  CardBody,
  FormGroup,
  Form,
  Input,
  Row,
  Col
} from "reactstrap";  

// import gql from "graphql-tag";
import { Mutation } from "react-apollo";
import { Link } from 'react-router-dom';
import  queries  from 'queries/Empresas.jsx';


class Formuser extends React.Component {

  showEmpresas() {

    // const data = (this.props.empresadata)
    // return data.data.map((prop, key) => {
    //     return (
    //         <tbody>
    //             <th key={prop._id} className="text-right">
    //                 {prop.nombre}
    //             </th>
    //             <th key={prop._id} className="text-right">
    //                 {prop.correo}
    //             </th>
    //             <th key={prop._id} className="text-right">
    //                 {prop.canton}
    //             </th>
    //             <th key={prop._id} className="text-right">
    //                 {prop.provincia}
    //             </th>
    //         </tbody>
    //     );
    // })
  }


  render() {

    let nombre,correo,usuario,provincia,canton,distrito;

    return (

      <Mutation mutation = {queries.addEmpresa} onCompleted={()=> this.props.history.push('/')}>
      <>
        <Col md="8">
          <Card>
            <CardHeader>
              <h5 className="title">Registro de empresas</h5>
            </CardHeader>
            <CardBody>
              <Form onSubmit={e=>{
                e.preventDefault();
                addBook({ variables: { nombre: isbn.value, title: title.value, author: author.value, description: description.value, publisher: publisher.value, published_year: parseInt(published_year.value) } });
                nombre.value = "";
                correo.value = "";
                usuario.value = "";
                provincia.value = "";
                canton.value = "";
                distrito.value = "";
            }}>
                })
              }} >
                <Row>
                  <Col className="px-1" md="3">
                    <FormGroup>
                      <label>Nombre</label>
                      <Input
                        defaultValue="michael23"
                        placeholder="nombre"
                        type="text"
                        name="nombre"
                        ref={node => {
                          nombre = node;}}
                      />
                    </FormGroup>
                  </Col>
                  <Col className="pl-1" md="4">
                    <FormGroup>
                      <label htmlFor="exampleInputEmail1">
                        Correo
                          </label>
                      <Input placeholder="Email" type="email" name="correo" />
                    </FormGroup>
                  </Col>
                  <Col className="pr-1" md="4">
                    <FormGroup>
                      <label>Usuario</label>
                      <Input
                        defaultValue="Mike"
                        placeholder="Company"
                        type="text"
                        name="usuario"
                      />
                    </FormGroup>
                  </Col>
                </Row>
                <Row>
                  <Col className="pl-1" md="4">
                    <FormGroup>
                      <label>Provincia</label>
                      <Input
                        defaultValue="Andrew"
                        placeholder="Last Name"
                        type="text"
                        name="provincia"
                      />
                    </FormGroup>
                  </Col>
                  <Col className="px-1" md="4">
                    <FormGroup>
                      <label>Cantón</label>
                      <Input
                        defaultValue="Andrew"
                        placeholder="Country"
                        type="text"
                        name="canton"
                      />
                    </FormGroup>
                  </Col>
                  <Col className="px-1" md="4">
                    <FormGroup>
                      <label>Distrito</label>
                      <Input
                        defaultValue="Andrew"
                        placeholder="Country"
                        type="text"
                        name="distrito"
                      />
                    </FormGroup>
                  </Col>
                </Row>
                <Row>
                  <Col md="12">
                    <FormGroup>
                      <label>Dirección</label>
                      <Input
                        defaultValue="Bld Mihail Kogalniceanu, nr. 8 Bl 1, Sc 1, Ap 09"
                        placeholder="Home Address"
                        type="text"
                        name="direccion"
                      />
                    </FormGroup>
                  </Col>
                </Row>
                <Row>
                  <Col className="pr-1" md="4">
                    <FormGroup>
                      <label>Distrito</label>
                      <Input
                        defaultValue="Mike"
                        placeholder="City"
                        type="text"
                        name="no"
                      />
                    </FormGroup>
                  </Col>

                  <Col className="pl-1" md="4">
                    <FormGroup>
                      <label>N.Cuenta</label>
                      <Input placeholder="x-xxxxxxx-xxx-x" type="text" name="cuenta" />
                    </FormGroup>
                  </Col>
                </Row>
                <Row>
                  <Col md="12">
                    <FormGroup>
                      <Button color="primary">Guardar</Button>
                    </FormGroup>
                  </Col>
                </Row>
              </Form>
            </CardBody>
          </Card>
        </Col>
      </>
      </Mutation>
    );
  }
}

// export default graphql(getEmpresas)(TablesEmpresas);
export default Formuser;
