*/
import React from "react";

// reactstrap components
import {
    Button,
    Card,
    CardHeader,
    CardBody,
    FormGroup,
    Form,
    Input,
    Row,
    Col
} from "reactstrap";

// core components
import PanelHeader from "components/PanelHeader/PanelHeader.jsx";
import Formuser from "components/forms/userform.jsx";
import Badgeuser from "components/badges/badgeUser";
import queries from 'queries/Empresas.jsx';
import { Query } from "react-apollo"
import uuid from 'uuid';


const query = queries.getEmpresa;

class UserEdit extends React.Component {

    state = {
        nombre: '',
        usuario: '',
        correo: '',
        provincia: '',
        canton: '',
        distrito: '',
    }

    componentDidMount() {


    }

    fetchdata() {

    }

    handleChange = e => {
        this.setState({
            ... this.state.form,
            [e.target.name]: e.target.value,
        });

    };

    render() {
        return (
            <>
                <PanelHeader size="sm" />
                <div className="content">
                    <Row>
                        <Query key={uuid()} query={query}>
                            {({ loading, error, data }) => {


                                if (loading) return <p>loading...</p>;
                                if (error) return <p>{error.message}</p>;
                                return (
                                    <React.Fragment>
                                        <Formuser onChange={this.handleChange} title={'Editar Empresa'} datos={this.state} />
                                        <Badgeuser datos={this.state} />
                                    </React.Fragment>
                                )
                            }}
                        </Query>
                    </Row>
                </div>
            </>
        );
    }
}

export default UserEdit;
