import {gql} from 'apollo-boost'
import {graphql} from 'react-apollo';


const getEmpresas= 
gql`
{
  empresas{
    nombre,
    distrito,
    correo,
    canton,
    provincia
    }
  }
  `;


  const getEmpresasAll= 
gql`
{
  empresas{
    _id,
    usuario,
    nombre,
    distrito,
    correo,
    canton,
    provincia
    }
  }
  `;



  const addEmpresa= 
gql`
mutation addEmpresa{ 
  addListing(
  nombre:String,
  distrito: String,
  correo: String,
  provincia:String,
  canton:String,
  ){
    nombre,distrito,correo,provincia,canton
  }
}
  `;


  const addEmpresas= 
gql`
mutation addEmpresa($nombre:String!,$usuario:String!, $distrito: String!,$correo: String!,$provincia:String!,$canton:String!)
{ 
  addListing(
  nombre:$nombre,
  usuario:$usuario
  distrito: $distrito,
  correo: $correo,
  provincia:$provincia,
  canton:$canton,
  ){
    nombre,usuario,distrito,correo,provincia,canton
  }
}
  `;

  const getEmpresa=

    gql` 
      query empresa($Id: String!)
       {
        empresa(Id:$Id){
          _id,
          nombre,
          usuario,
          distrito,
          correo,
          canton,
          provincia   
        }
      }
    `;

    const updateEmpresa=

    gql` 
      mutation empresa($Id: String!,$nombre:String!,$usuario:String!, $distrito: String!,$correo: String!,$provincia:String!,$canton:String!)
       {
        updateEmpresa(
          Id:$Id,
          nombre:$nombre,
          usuario:$usuario,
          distrito:$distrito,
          correo:$correo,
          canton:$canton,
          provincia:$provincia 
        )
        {
          Id,nombre,distrito,correo,provincia,canton
        }
      }
    `;

    const deleteEmpresa=
    gql`

    mutation empresa($Id: String!) {
      deleteEmpresa(Id: $Id) {
        Id
      }
    }

    `




  export default ({getEmpresasAll,getEmpresas,addEmpresas,getEmpresa,updateEmpresa});
