*/
import React from "react";

// reactstrap components
import {
    Button,
    Card,
    CardHeader,
    CardBody,
    FormGroup,
    Form,
    Input,
    Row,
    Col
} from "reactstrap";
import { gql } from 'apollo-boost'


// core components
import PanelHeader from "components/PanelHeader/PanelHeader.jsx";
import Formuser from "components/forms/userform.jsx";
import Badgeuser from "components/badges/badgeUser";
import queries from 'queries/Empresas.jsx';
import { Query } from "react-apollo"
import uuid from 'uuid';


const getEmpresa = gql`${queries.getEmpresa}`;



class UserEdit extends React.Component {

    state = {
        nombre: '',
        usuario: '',
        correo: '',
        provincia: '',
        canton: '',
        distrito: '',
    }

    componentDidMount() {

        const id= this.props    
        console.log(id);

    }

    fetchdata() {

    }

    handleChange = e => {
        this.setState({
            ... this.state.form,
            [e.target.name]: e.target.value,
        });

    };

    render() {

        return (
            <>
                <PanelHeader size="sm" />
                <div className="content">
                    <Row>
                        <Query key={uuid()} query={getEmpresa} variables={this.props.match.params._id} >
                            {({ loading, error, data }) => {

                                const empresa=data;
                                console.log(data)

                                if (loading) return <p>loading...</p>;
                                if (error) return <p>{error.message}</p>;
                                return (
                                    <React.Fragment>
                                        <Formuser onChange={this.handleChange} title={'Editar Empresa'} datos={empresa} />
                                        <Badgeuser datos={empresa} />
                                    </React.Fragment>
                                )
                            }}
                        </Query>
                    </Row>
                </div>
            </>
        );
    }
}

export default UserEdit;
