import { Table } from "reactstrap";
import { thead, tbody } from "variables/general";
import React from "react";
import { Query } from "react-apollo"
import { graphql } from 'react-apollo';
import queries from 'queries/Empresas.jsx';
import {gql} from 'apollo-boost'



const getEmpresas= 
gql`
{
  empresas{
    nombre,
    distrito,
    correo,
    canton,
    provincia
    }
  }
  `;

class TablesEmpresas extends React.Component {

    showEmpresas() {
        const data = (this.props.empresadata)
        return data.data.map((prop, key) => {
            return (

                <Query query={getEmpresas}>
                    {({ loading, error, data }) => {
                      return(
                          <tbody key={prop._id}>
                                <tr>    
                                    <th key={prop._id} className="text-right">
                                        {console.log()
                                        }
                                    </th>


                                    <th key={prop._id} className="text-right">
                                        {prop.correo}
                                    </th>

                                    <th key={prop._id} className="text-right">
                                        {prop.canton}
                                    </th>

                                    <th key={prop._id} className="text-right">
                                        {prop.provincia}
                                    </th>
                                </tr>
                            </tbody>
                      )
                    }}
                </Query>
            )
        })
    }

    render() {
        return (
            <>
                <Table responsive>
                    <thead className="text-primary">
                        <tr>
                            {thead.map((prop, key) => {
                                if (key === thead.length - 1)
                                    return (
                                        <th key={key} className="text-right">
                                            {prop}
                                        </th>
                                    );
                                return <th key={key}>{prop}</th>;
                            })}
                        </tr>
                    </thead>
                    {this.showEmpresas()}
                </Table>
            </>
        );
    }
}

export default graphql(getEmpresas)(TablesEmpresas);
