import React from 'react';
import uuid from 'uuid';
import { Button, Card, CardHeader, CardBody, FormGroup, Form, Input, Row, Col } from "reactstrap";
import { Link } from 'react-router-dom';
import { Mutation } from "react-apollo";
import queries from 'queries/Empresas.jsx';


const item = (props) => {


  const element = props.variables;
  const query=queries.deleteEmpresa;

  return (

    <tr key={uuid()}>
      <th key={(element._id)} className="text-right">
        {(element.nombre)}
      </th>

      <th key={(element._id)} className="text-right">
        {(element.correo)}
      </th>

      <th key={(element._id)} className="text-right">
        {(element.canton)}
      </th>

      <th key={(element._id)} className="text-right">
        {(element.provincia)}
      </th>
      <th>
        <Link
          className="text-reset text-decoration-none ml-1 btn btn-outline-info"
          to={`/admin/user-edit/${(element._id)}`}
          params={{ empresaid: element._id }}
        >Edit
        </Link>
        <Mutation mutation={query} variables={{ Id}} onCompleted={createNotification("success")} onError={createNotification("error")}>
          {postEmpresa =>
            <Link
              className="text-reset text-decoration-none ml-1 btn btn-outline-danger"
              to={`/user-page/:${element._id}`}
            >Delete
            </Link>
          }
        </Mutation>

      </th>
    </tr>
  )
};

export default item;
