import { Table } from "reactstrap";
import { thead, tbody } from "variables/general";
import React from "react";
import { Query } from "react-apollo"
import { graphql } from 'react-apollo';


class TablesEmpresas extends React.Component {

    showEmpresas() {
        const data = (this.props.empresadata)
        const query = (this.props.empresadata.query)
        return data.data.map((prop, key) => {
            return (

                <Query query={query}>
                    {({ loading, error, data }) => {
                      return(

                          <tbody key={prop._id}>
                              <h1>{data.nombre}</h1>
                                <tr>    
                                    <th key={prop._id} className="text-right">
                                        {prop.nombre}
                                    </th>


                                    <th key={prop._id} className="text-right">
                                        {prop.correo}
                                    </th>

                                    <th key={prop._id} className="text-right">
                                        {prop.canton}
                                    </th>

                                    <th key={prop._id} className="text-right">
                                        {prop.provincia}
                                    </th>
                                </tr>
                            </tbody>
                      )
                    }}
                </Query>
            )
        })
    }

    render() {
        return (
            <>
                <Table responsive>
                    <thead className="text-primary">
                        <tr>
                            {thead.map((prop, key) => {
                                if (key === thead.length - 1)
                                    return (
                                        <th key={key} className="text-right">
                                            {prop}
                                        </th>
                                    );
                                return <th key={key}>{prop}</th>;
                            })}
                        </tr>
                    </thead>
                    {this.showEmpresas()}
                </Table>
            </>
        );
    }
}

export default graphql(getEmpresas,query)(TablesEmpresas);
