import { Table } from "reactstrap";
import { thead, tbody } from "variables/general";
import React from "react";
import { Query } from "react-apollo"
import { graphql } from 'react-apollo';
import queries from 'queries/Empresas.jsx';
import { gql } from 'apollo-boost'
import uuid from 'uuid';
import { useQuery, useMutation, useSubscription } from "@apollo/react-hooks";
import EmpresaItem from './Empresa/EmpresaItem'

const getEmpresas = gql`${queries.getEmpresas}`;

class TablesEmpresas extends React.Component {

    state = {
        loading: true,
        error: null,
        empresas: [],
    };

    componentWillMount() {


    }

    componentDidMount() {

        // this.fetchData();
        this.intervalId = setInterval(this.getQueryEmpresas, 5000);
        // this.intervalId = setInterval(
        //     function () {
        //        const {datos}=useQuery(getEmpresas)
        //        this.setState({empresas:datos});
        //     }, 5000
        // )
    }

    // getData(){
    //     this.intervalId = setInterval(
    //         function () {
    //            const {datos}=useQuery(getEmpresas)
    //            this.setState({empresas:datos});
    //         }, 5000
    //     )}

    getQueryEmpresas(){
        return (
            <Query key={3} query={getEmpresas}>
                {({ loading, error, data }) => {

                    if (loading) return <p>loading...</p>;
                    if (error) return <p>{error.message}</p>;


                    return (
                        <tbody key={9}>
                            {data.empresas.map((element) => {
                                return (

                                        <EmpresaItem />

                                )
                            })}
                        </tbody>
                    )
                }}
            </Query>
        )
    }


    componentWillUnmount() {
        // clearInterval(this.intervalId);
    }

    showEmpresas() {

        var props = this.props;
        // const datos=[...this.state.empresas];


        if (props.datos) {
            // props = this.props.datos;
            console.log('if');
            return (
                <tbody key={9}>
                    {this.props.datos.map((element) => {
                        return (

                            <tr key={uuid()}>
                                <th key={element._id} className="text-right">
                                    {element.nombre}
                                </th>

                                <th key={element._id} className="text-right">
                                    {element.correo}
                                </th>

                                <th key={element._id} className="text-right">
                                    {element.canton}
                                </th>

                                <th key={element._id} className="text-right">
                                    {element.provincia}
                                </th>
                            </tr>
                        )
                    })}
                </tbody>
            )
        }
        else {
            console.log('else');
            this.getQueryEmpresas();
        }
    }


    render() {


        return (
            <>
                <Table key={2} responsive>
                    <thead className="text-primary">
                        <tr>
                            {thead.map((prop, key) => {
                                if (key === thead.length - 1)
                                    return (
                                        <th key={key} className="text-right">
                                            {prop}
                                        </th>
                                    );
                                return <th key={key}>{prop}</th>;
                            })}
                        </tr>
                    </thead>
                    {this.showEmpresas()}
                </Table>
            </>
        );
    }
}

export default graphql(getEmpresas)(TablesEmpresas);
