import React from "react";

// reactstrap components
import {
  Button,
  Card,
  CardHeader,
  CardBody,
  FormGroup,
  Form,
  Input,
  Row,
  Col
} from "reactstrap";

// core components
import PanelHeader from "components/PanelHeader/PanelHeader.jsx";
import Formuser from "components/forms/userform.jsx";
import Badgeuser from "components/badges/badgeUser";
import queries from 'queries/Empresas.jsx';

class User extends React.Component {

  state = {
    nombre: '',
    usuario: '',
    correo: '',
    provincia: '',
    canton: '',                                               
    distrito: '',
  } 

  handleChange=e=>{
    this.setState({
             ... this.state,
            [e.target.name]:e.target.value,
    });
};

  render() {

    const query=gql`{queries.addEmpresas`;
    return (
      <>
        <PanelHeader size="sm" />
        <div className="content">
          <Row>
            <Formuser onChange={this.handleChange} title={'Registro de empresas'} query={query}  datos={this.state}/>
            <Badgeuser datos={this.state} />
          </Row>
        </div>
      </>
    );  
  }
}

export default User;
