import React from "react";
import ApolloClient from 'apollo-boost';
import { ApolloProvider } from 'react-apollo';



// reactstrap components
import {
  Card,
  CardBody,
  CardHeader,
  CardTitle,
  // Table,
  Row,
  Col
} from "reactstrap";

// core components
import PanelHeader from "components/PanelHeader/PanelHeader.jsx";
import Table from 'components/tables/Tablelist.jsx';
import { thead, tbody } from "variables/general";
import  queries  from 'queries/Empresas.jsx';
import { graphql } from 'react-apollo';

//Se importa el query
const getEmpresas =queries


class RegularTables extends React.Component {

  state = {
    loading: true,
    error: null,
    data: undefined,
  };

  componentDidMount() {
     this.fetchData();

  }

  fetchData = async () => {
    const data = (this.props.data)
     this.setState({ loading: data.loading, error: null });

    try {

      if(data.loading){

        const datos = data.empresas;
        console.log(datos);
        this.setState({ loading: data.loading, data: datos });
      }
    } catch (error) {
      this.setState({ loading: data.loading, error: error });
    }
  };

  render() {
    return (
      <>
        <PanelHeader size="sm" />
        <div className="content">
          <Row>
            <Col xs={12}>
              <Card>
                <CardHeader>
                  <CardTitle tag="h4">Empresas registradas</CardTitle>
                </CardHeader>
                <CardBody>
                  <Table />
                </CardBody>
              </Card>
            </Col>
            <Col xs={12}>
              <Card className="card-plain">
                <CardHeader>
                  <CardTitle tag="h4">Table on Plain Background</CardTitle>
                  <p className="category"> Here is a subtitle for this table</p>
                </CardHeader>
                <CardBody>
                  <Table empresas={this.state.data}/>
                </CardBody>
              </Card>
            </Col>
          </Row>
        </div>
      </>
    );
  }
}

// export default RegularTables;
export default graphql(getEmpresas)(RegularTables);
